<?phpif ($_REQUEST[submit]){    foreach($kerdesek as $key => $value){        if ($kerdesek[$key] != '0'){             $figy_uzenet .= $value.', ';  //ha a kérdésre nincs válasz, akkor feljegyezzük		 }    }              if ($figy_uzenet){	  $figy_uzenet = substr($figy_uzenet, 0, -2); //eltávolítjuk az utolsó két karaktert	  $figy_uzenet = '<h3><br />'.$lang['Az alábbi kérdésekre nem válaszoltál'].':</h3>'.$figy_uzenet;       }    if ((!$_REQUEST[email]) AND (!$_REQUEST[ok])){        if ($kapcs_email == '1'){            $figy_uzenet .= '<br /><br />'.$lang['E-mail címed hiányzik!'];            $log->write('x', 'E-mail nem volt megadva');        }    }	if ($_REQUEST[email]){	  if (!filter_var($_REQUEST[email], FILTER_VALIDATE_EMAIL)) {		 $figy_uzenet .= '<br /><br />'.$lang['A megadott e-mail cím helytelen'];	  }	}}if (!$figy_uzenet){    $_REQUEST['b'] = '1';}$smarty->assign('lang', $lang);