<?phpif ($_REQUEST[keres]){   $keres_szur = "AND (cim_hu LIKE '%$_REQUEST[keres]%' OR cim_en LIKE '%$_REQUEST[keres]%' OR cim_de LIKE '%$_REQUEST[keres]%'           OR leiras_hu LIKE '%$_REQUEST[keres]%' OR leiras_en LIKE '%$_REQUEST[keres]%' OR leiras_de LIKE '%$_REQUEST[keres]%')";}$result = "SELECT sorszam FROM kerdoivek WHERE status = '1' AND nyilvanos = '1' $keres_szur";$navsav = new navsav();$navsav->create_navsav($result, $_REQUEST['lap'], 5, $kiemeltx, $_REQUEST[kategoriaszures]);$result = mysql_query($result."ORDER BY kiemeles DESC, sorszam DESC LIMIT $navsav->tol, $navsav->ig");while ($next_element = mysql_fetch_array($result)){  //5 darab kérdőív sorszámon megy végig    $obj_kerdoiv = new kerdoiv;    $obj_kerdoiv->load($next_element[sorszam]);    $obj_array[] = $obj_kerdoiv;}$talalatszam .= '<div>A keresés eredménye: '.$navsav->termekdb.' db találat</div>';    $smarty->assign('lang', $lang);$smarty->assign('obj_array', $obj_array);$smarty->assign('keres', $_REQUEST[keres]);$smarty->assign('navsav', $navsav->lapszamsor);$smarty->assign('talalatszam', $talalatszam);$smarty->assign('nyilvanos_kerdoivek', $nyilvanos_kerdoivek);$smarty->assign('nyilvanos_kepek', $nyilvanos_kepek);$tartalom = $smarty->fetch('templates/nyilvanos.tpl');